import React from "react";
import '../styles/home.css';

import { Container, Row, Col } from 'reactstrap';
import heroImg from '../assets/images/hero-img01.jpg';
import heroImg02 from '../assets/images/hero-img02.jpg';
import heroVideo from '../assets/images/hero-video.mp4';
import worldImg from '../assets/images/world.png';
import Subtitle from '../shared/Subtitle';

import SearchBar from "../shared/SearchBar.jsx";
import ServiceList from "../services/ServiceList";

const Home = () => {
  return (

    <>

    {/* Section */}
    
    <section>
      <Container>
        <Row>
          <Col lg='6'>
            <div className="hero_content">
              <div className="hero_subtitle d-flex align-items-center">
                <Subtitle subtitle={'Know Before You Go'} />
                <img src={worldImg} alt="world icon" />
              </div>
              <h1>
                something about the hotel <span className="highlight">Hotel</span>
              </h1>
              <p>
                Lorem ipsum, dolor sit amet consectetur adipisicing elit. Illo cumque repudiandae voluptatibus 
                aut voluptatem, ex, officiis blanditiis ullam itaque sequi, corporis explicabo magni quisquam nulla 
                excepturi sit ut voluptatum laborum?
              </p>
            </div>
          </Col>
              <Col lg='2'>
                <div className="hero__img-box">
                  <img src={heroImg} alt="" /> 
                </div>
              </Col>
              <Col lg='2'>
                <div className="hero__img-box">
                  <video src={heroVideo} alt="" controls />
                </div>
              </Col>
              <Col lg='2'>
                <div className="hero__img-box">
                  <img src={heroImg02} alt="" />
                </div>
              </Col>  
              <SearchBar/>
        </Row>
      </Container>
    </section>

    {/* Section */}

    <section>
      <Container>
        <Row>
          <Col lg="3">
            <h5 className="services__subtitle">What we serve</h5>
            <h2 className="services__title">We offer our best services</h2>
          </Col>
          <ServiceList />
          </Row>
      </Container>
    </section>

    {/*new section*/}

    

    {/*new section*/}
    </>
  );
};

export default Home;
