import React from "react";
import ServiceCard from "./ServiceCard";
import { Col } from "reactstrap";

import wellnessImg from "../assets/images/weather.png";
import guideImg from "../assets/images/guide.png";
import customizationImg from "../assets/images/customization.png";

const servicesData = [
  {
    imgUrl: wellnessImg,
    title: "Wellness Treatments & Activities",
    desc: "Wide range of treatments & activities to enhance your health and wellbeing."
  },
  {
    imgUrl: guideImg,
    title: "Travel Guide",
    desc: "Your ultimate guide to travel destinations."
  },
  {
    imgUrl: customizationImg,
    title: "Customization",
    desc: "Personalize your travel experience."
  }
];

const ServiceList = () => {
    return (
      <>
        {servicesData.map((item, index) => (
          <Col lg="3" key={index}>
            <ServiceCard item={item} />
          </Col>
        ))}
      </>
    );
  };
  

export default ServiceList;
