import { Card,CardBody } from 'reactstrap'
import {Link} from "react-router-dom"

const RoomCard = ({ room }) => {

    const { id, title, photo, price, featured, avgRating, reviews } = room;

    return (
        <div className="room___card">
            <Card>
                <div className="room___img">
                    <img src={photo} alt="room-img" /> <span>Featured</span>
                </div> I
            </Card>
        </div>
    );
};        
    
export default RoomCard;
